class TrieNode:
    def __init__(self, words: List[str] = None):
        self.children = {}
        self.a_word = False
        if words:
            for word in words:
                self.add(word)

    def add(self, word: str):
        cur = self
        for c in word:
            if c not in cur.children:
                cur.children[c] = TrieNode()
            cur = cur.children[c]
        cur.a_word = True


class Encrypter:
    def __init__(self, keys: List[str], values: List[str], dictionary: List[str]):
        self.keylookup = {key: val for key, val in zip(keys, values)}
        self.vallookup = defaultdict(list)
        for i, val in enumerate(values):
            self.vallookup[val].append(keys[i])
        self.trie = TrieNode(dictionary)

    @cache
    def encrypt(self, word1: str) -> str:
        ret = []
        for c in word1:
            if c not in self.keylookup:
                return ""
            ret.append(self.keylookup[c])
        return "".join(ret)

    @cache
    def decrypt(self, word2: str) -> int:
        chunks = (word2[i : i + 2] for i in range(0, len(word2), 2))
        candidates = list(self.vallookup[s] for s in chunks)
        N = len(candidates)

        def dfs(root: TrieNode, i):
            if i == N:
                return int(root.a_word)

            count = 0
            for key in candidates[i]:
                if key in root.children:
                    count += dfs(root.children[key], i + 1)

            return count

        return dfs(self.trie, 0)
