	Map<Character, String> kvMap = new HashMap<>();
    Map<String, List<Character>> vkMap = new HashMap<>();
    TrieNode root = new TrieNode();
    Map<String, Integer> cache = new HashMap<>();

    public Encrypter(char[] keys, String[] values, String[] dictionary) {
        for (int i = 0, len = keys.length; i < len; ++i) {
            char key = keys[i];
            String value = values[i];
            kvMap.put(key, value);
            List<Character> characters = vkMap.getOrDefault(value, new ArrayList<>());
            characters.add(key);
            vkMap.put(value, characters);
        }
        for (String dic : dictionary) {
            build(root, dic);
        }
    }

    public String encrypt(String word1) {
        StringBuilder sb = new StringBuilder();
        for (char c : word1.toCharArray()) {
            String s = kvMap.get(c);
            if (s == null) {
                return "";
            }
            sb.append(s);
        }
        return sb.toString();
    }

    public int decrypt(String word2) {
        Integer integer = cache.get(word2);
        if (integer == null) {
            int dfs = dfs(word2, 0, new StringBuilder());
            cache.put(word2, dfs);
            return dfs;
        }
        return integer;
    }

        private int dfs(String word2, int index, StringBuilder sb) {
        if (index == word2.length()) {
            return search(root, sb.toString(), 0) ? 1 : 0;
        }
        if (index > 0 && !searchPrefix(root, sb.toString(), 0)) {
             return 0;
        }
        int count = 0;
        String substr = word2.substring(index, index + 2);
        List<Character> characters = vkMap.get(substr);
        if (characters == null) {
            return 0;
        }
        for (Character c : characters) {
            sb.append(c);
            count += dfs(word2, index + 2, sb);
            sb.deleteCharAt(sb.length() - 1);
        }
        return count;
    }

    private void build(TrieNode root, String word) {
        TrieNode curr = root;
        for (char c : word.toCharArray()) {
            Map<Character, TrieNode> children = curr.getChildren();
            TrieNode node = children.get(c);
            if (node == null) {
                node = new TrieNode();
                children.put(c, node);
            }
            curr = node;
        }
        curr.setWord(true);
    }

    private boolean search(TrieNode root, String word, int index) {
        if (index == word.length()) {
            return false;
        }
        char c = word.charAt(index);
        TrieNode node = root.getChildren().get(c);
        if (node == null) {
            return false;
        }
        if (index == word.length() - 1 && node.isWord) {
            return true;
        }
        return search(node, word, index + 1);
    }

    private boolean searchPrefix(TrieNode root, String word, int index) {
        if (index == word.length()) {
            return true;
        }
        char c = word.charAt(index);
        TrieNode node = root.getChildren().get(c);
        if (node == null) {
            return false;
        }
        return searchPrefix(node, word, index + 1);
    }

