        for (int idx = 0; idx < word1.length(); idx++) {
            result.append(enc.getOrDefault(word1.charAt(idx), DEFAULT_ENCODE_STRING));
        }
        return result.toString();
    }

    public int decrypt(String word2) {
        return countMap.getOrDefault(word2, 0);
    }
