            let t = this.encrypt(s);
            if (2 * s.length == t.length)
                this.cnt.set(t, 1 + (this.cnt.get(t) || 0));
        }
    }
    encrypt = s => s.split('').map(c => this.m.get(c) || '').join('');
    decrypt = s => this.cnt.get(s) || 0;
}
