use std::collections::HashMap;
struct Encrypter {
    m: HashMap<char, String>,
    cnt: HashMap<String, i32>
}
type VC = Vec<char>;
type VS = Vec<String>;
impl Encrypter {
    fn new(C: VC, S: VS, A: VS) -> Self {
        let mut m = HashMap::new();
        let mut cnt = HashMap::new();
        for (c, s) in C.iter().zip(S.iter()) {
            m.insert(*c, s.clone());
        }
        let mut me = Encrypter{ m, cnt };
        for s in A {
            let t = Self::encrypt(&mut me, s.clone());
            if 2 * s.len() == t.len() {
                *me.cnt.entry(t).or_insert(0) += 1;
            }
        }
        me
    }
    fn encrypt(&mut self, s: String) -> String {
        let mut t = String::new();
        for c in s.chars() {
            if let Some(x) = self.m.get(&c) {
                t.push_str(x.as_str());
            }
        }
        t
    }
    fn decrypt(&mut self, s: String) -> i32 {
        *self.cnt.entry(s).or_insert(0)
    }
}
