class Encrypter {
public:
    using VC = vector<char>;
    using VS = vector<string>;
    using Map = unordered_map<char, string>;
    using Counter = unordered_map<string, int>;
    Map m;
    Counter cnt;
    Encrypter(VC& C, VS& S, VS& A) {
        for (auto i{ 0 }; i < C.size(); ++i)
            m[C[i]] = S[i];
        for (auto& s: A) {
            auto t = encrypt(s);
            if (2 * s.size() == t.size())
                ++cnt[t];
        }
    }
    string encrypt(string s) {
        ostringstream os;
        transform(s.begin(), s.end(), ostream_iterator<string>(os, ""), [&](auto c) { return m[c]; });
        return os.str();
    }
    int decrypt(string s) {
        return cnt[s];
    }
};
