	class TrieNode{
        TrieNode[] children;
        boolean isWord;

        public TrieNode(){
            children = new TrieNode[26];
            isWord = false;
        }
    }

    Map<Character, String> key2value;
    Map<String, Set<Character>> value2key;
    TrieNode root;

    private void insert(String word, TrieNode root){
        TrieNode cur = root;
        for(int i = 0; i < word.length(); i++){
            char c = word.charAt(i);
            if(cur.children[c - 'a'] == null)
                cur.children[c - 'a'] = new TrieNode();
            cur = cur.children[c - 'a'];
            if(i == word.length() - 1){
                cur.isWord = true;
            }
        }
    }

    public Encrypter(char[] keys, String[] values, String[] dictionary) {
        key2value = new HashMap<>();
        value2key = new HashMap<>();
        root = new TrieNode();
        for(int i = 0; i < keys.length; i++){
            char key = keys[i];
            String value = values[i];
            key2value.put(key, value);
            value2key.putIfAbsent(value, new HashSet<Character>());
            value2key.get(value).add(key);
        }
        for(String s: dictionary){
            insert(s, root);
        }
    }

    public String encrypt(String word1) {
        StringBuilder sb = new StringBuilder();
        for(int i = 0; i < word1.length(); i++){
            sb.append(key2value.get(word1.charAt(i)));
        }
        return sb.toString();
    }

    public int decrypt(String word2) {
        int[] res = new int[1];
        dfs(word2, 0, res, this.root);
        return res[0];
    }

    private void dfs(String s, int idx, int[] res, TrieNode root){
        if(idx == s.length() && root.isWord){
            res[0]++;
            return;
        }
        if(idx >= s.length()){
            return;
        }
        String str = s.substring(idx, idx + 2);
        if(!value2key.containsKey(str)){
            return;
        }
        for(char cha: value2key.get(str)){
            if(root.children[cha - 'a'] != null){
                dfs(s, idx + 2, res, root.children[cha - 'a']);
            }
        }
    }

