        this.deMap = new HashMap<>();
        this.dict = new HashSet<>();

        for(int i = 0; i < keys.length; i++) {
            enMap.put(keys[i], values[i]);
            if(!deMap.containsKey(values[i])) {
                deMap.put(values[i], new ArrayList<>());
            }
            deMap.get(values[i]).add(keys[i]);
        }   

        for(String str : dictionary) {
            dict.add(str);
        }  

        this.root = new TrieNode();
        for(String str : dictionary){
            root.insert(root, str);
        }

    }

    public String encrypt(String word1) {
        StringBuilder sol = new StringBuilder();
        for(char ch : word1.toCharArray()) {
            sol.append(enMap.get(ch));
        }
        return sol.toString();
    } 

    public int decrypt(String word2) {
        int[] res = {0};
        StringBuilder sol = new StringBuilder();
        TrieNode curRoot = root;

        dfs(word2, curRoot, 0, sol, deMap, dict, res);
        return res[0];
    }

    private void dfs(String word, TrieNode curRoot, int index, StringBuilder sol, Map<String, List<Character>> deMap, Set<String> dict, int[] res) {

        if(index >= word.length() - 1) {
            String temp = sol.toString();
            if(dict.contains(temp)) {
                res[0]++;
            }
            return;
        }

        String cur = word.substring(index, index + 2);

        List<Character> list = deMap.get(cur);
        int len = sol.length();
        if(list == null) {
            sol.append(cur);
            dfs(word, curRoot, index + 2, sol, deMap, dict, res);
            sol.setLength(len);
        } else {
            for(char c : list) {
                if(curRoot.children[c - 'a'] == null) {
                    continue;
                }
                sol.append(c);
                dfs(word, curRoot.children[c -'a'], index + 2, sol, deMap, dict, res);
                sol.setLength(len);
            }
        }    
    }

    class TrieNode {
        boolean isWord;
        TrieNode[] children;
        public TrieNode() {
            this.isWord = false;
            this.children = new TrieNode[26];
        }

        public TrieNode insert(TrieNode root, String word) {
            TrieNode cur = root;

            for(int i = 0; i < word.length(); i++) {
                if(cur.children[word.charAt(i) - 'a'] == null) {
                    cur.children[word.charAt(i) - 'a'] = new TrieNode();
                }
                cur = cur.children[word.charAt(i) - 'a'];
            }
            cur.isWord = true;
            return root;
        }
    }
}


