#### Trie solution 
```
class Encrypter {
public:
    vector<string> k2v;
    unordered_map<string,vector<int>> v2k; 
    class Node {
    public:
        bool leaf = false; 
        vector<Node*> children;
        Node() : leaf{false}, children(26,nullptr) {}
    };
    class Trie {
    public:
        Node* root = new Node(); 
        void insert(const string& str) {
            auto t = root; 
            for(char ch : str) {
                if(t->children[ch - 'a'] == nullptr) {
                    t->children[ch-'a'] = new Node(); 
                }
                t = t->children[ch-'a'];
            }
            t->leaf = true; 
        }
    };
    Trie trie; 
    Encrypter(vector<char>& keys, vector<string>& values, vector<string>& dictionary) : k2v(26,""){
        for(int i = 0;i<keys.size();i++) {
            k2v[keys[i] - 'a'] = values[i];
            v2k[values[i]].push_back(keys[i] - 'a');
        }
        for(auto& str : dictionary) {
            trie.insert(str);
        }
    }

    string encrypt(string word1) {
        string ret;
        for(char ch : word1) {
            ret += k2v[ch-'a'];
        }
        return ret; 
    }

    int f(Node* node, const string& word2, int i) {
        if(node == nullptr) {
            return 0; 
        }
        if(i == word2.size()) {
            if(node->leaf) {
                return 1;
            }
            return 0; 
        }
        auto v = word2.substr(i,2);
        int ret = 0; 
        for(int k : v2k[v]) {
            ret += f(node->children[k],word2,i+2);
        }
        return ret; 
    }
    int decrypt(string word2) {
        return f(trie.root,word2,0); 
    }
};

