class Trie {
public: 
    Trie() {
        root = new TrieNode();
    }

    TrieNode* getRoot()
    {
        return root;
    }

    void insert(string word) {
        TrieNode* cur = root;
        for (auto c : word)
        {
            if (!cur->next[c - 'a'])
                cur->next[c - 'a'] = new TrieNode();
            cur = cur->next[c - 'a'];
        }
        cur->is_word = true;
    }

    TrieNode* find(char c, TrieNode* node)
    {
        if (node->next[c - 'a'])
            return node->next[c - 'a'];
        else 
            return nullptr;
    }

private: 
    TrieNode* root;
};

