        int n = word2.length(), m = n / 2;
        vector<vector<char>> k(m);
        for (int i = 0, j = 0; i < n; i += 2, j++)
        {
            k[j] = v2k[word2.substr(i, 2)];
            if (k[j].size() == 0)
