class Encrypter {
public:
    unordered_map <char, string> mp; // Will be used while encrypting. 
    unordered_map <string, unordered_set<char>> allTwotoOne; // stores for each string of len 2, possible characteres it can map to
    unordered_set <string> dict; // our dictionary which includes valid words after decrypting.

    Encrypter(vector<char>& keys, vector<string>& values, vector<string>& dictionary) {
        for (int i = 0; i < keys.size(); ++i) {
            mp[keys[i]] = values[i]; // will be used in encryption.
        }
        for (int i = 0; i < values.size(); ++i) {
            allTwotoOne[values[i]].insert(keys[i]); // all possible chars which this encrypted 2 len string can decrpt to.
        }
        for (auto x : dictionary) {
            dict.insert(x);   // just making it global.
        }
    }   

    /*
        We can use mp to just replace each character with susbtrs of len 2.
    */

    string encrypt(string word1) {
        string res = "";
        for (auto x : word1) {
            res += mp[x];
        }
        return res;
    }
    /*
        Returns the number of possible strings word2 could decrypt to that also appear in dictionary.
    */
    int decrypt(string word2) {
        int ways = 0;
        int enc_len = word2.size() / 2;
        // check for each word in dictionary, can it be formed while decyrpting string in given manner.
        for (auto x : dict) { 
            if (x.size() != enc_len) continue; // prune as fast as possible.
            int i = 0;
            int can = 1;
            for (auto ch : x) {
                if (!allTwotoOne[word2.substr(i, 2)].count(ch)) { // if this 2 len substr cant decrypt to the current char of current string in dict, then break(prune)
                    can = 0;
                    break;
                } else { // go to next 2 len substr.
                    i += 2;
                }
            }
            ways += can;
        }
        return ways;
    }
};

