public class Encrypter {

    HashSet<string> pString;
    // dictionary used for encryption
    Dictionary<string, HashSet<char>> rDict = new Dictionary<string, HashSet<char>>();
    // dictionary used for decryption
    Dictionary<char, string> fDict = new Dictionary<char, string>();

    public Encrypter(char[] keys, string[] values, string[] dictionary) {
        pString = new HashSet<string>(dictionary);
        for (int i = 0; i < keys.Length; i++){
            fDict.Add(keys[i], values[i]);
            if (!rDict.ContainsKey(values[i]))
                rDict.Add(values[i], new HashSet<char>());
            rDict[values[i]].Add(keys[i]);
        }
    }

    public string Encrypt(string word1) {
        string output = "";
        foreach (char c in word1){
            output += fDict[c];
        }
        return output;
    }

    // Because the dictionary is smaller than 100, we basically just loop the dictionary for each number, worst time is just 100 * 100
    public int Decrypt(string word2) {
        List<string> hs = new List<string>(pString);
        int len = word2.Length / 2;
        for (int i = 0; i < len; i++){
            string str = word2.Substring(i*2, 2);
            if (!rDict.ContainsKey(str))
                return 0;
            HashSet<char> ch = rDict[str];
            List<string> temp = new List<string>();
            foreach (string h in hs){
                if (h.Length > i && ch.Contains(h[i]))
                    temp.Add(h);
            }
            if (temp.Count == 0)
                return 0;
            hs = temp;
        }
        List<string> output = new List<string>();
        foreach (string k in hs){
            if (k.Length == len)
                output.Add(k);
        }

        return output.Count;
    }
}

