          const nextNode = trieNode.children[key];
          if (nextNode) possible.push(nextNode);
        }
      }
      if (possible.length === 0) break;
    }
    return possible.filter(({ leaf }) => leaf).length;
  }
}

interface TrieNode {
  char: string;
  leaf: boolean;
  children: { [char: string]: TrieNode };
}

const buildTrie = (
  words: string[],
  parent: TrieNode = { char: "", leaf: false, children: {} }
) => {
  const wordMap: { [char: string]: string[] } = {};
  for (const word of words) {
    if (word === "") {
      parent.leaf = true;
      continue;
    }
    const char = word[0];
    const arr = wordMap[char] ?? [];
    arr.push(word.slice(1));
    wordMap[char] = arr;
  }
  for (const [char, words] of Object.entries(wordMap)) {
    const trieNode = {
      char,
      leaf: false,
      children: {},
    };
    buildTrie(words, trieNode);
    parent.children[char] = trieNode;
  }
  return parent;
};

```

