    private _decryptedWordMap: Map<string, number>;

    constructor(keys: string[], values: string[], dictionary: string[]) {
        this._keyMap = new Map();
        this._decryptedWordMap = new Map();

        keys.forEach((key, i) => {
            this._keyMap.set(key, values[i]);
        });

        dictionary.forEach((word) => {
