        word1.split("").forEach((char) => {
            decryptedWord1 += this._keyMap.get(char);
        });

        return decryptedWord1;
    }

    decrypt(word2: string): number {
        const decryptedWordNum = this._decryptedWordMap.get(word2);

        if (decryptedWordNum)
            return decryptedWordNum
        else
            return 0;
