class Trie {
public:
    int cnt;
    Trie* child[26];
    Trie(): cnt(0), child{} {};
};

class Encrypter {
public:
    string* map[26];
    Trie* root;
    Encrypter(vector<char>& keys, vector<string>& values, vector<string>& dictionary): map{nullptr}, root{nullptr} {
        for (int i = 0, size = keys.size(); i < size; i+=1) map[keys[i]-'a'] = &values[i];
        this -> root = new Trie();
        auto insert = [&] (const string& word) -> void {
            if (word.empty()) return;
            Trie* cur = this -> root;
            for (auto& ch: word) {
                if (cur -> child[ch-'a'] == nullptr) cur -> child[ch-'a'] = new Trie();
                cur = cur -> child[ch-'a'];
            }
            cur -> cnt += 1;
        };
        for (auto& word: dictionary) insert(this -> encrypt(word));
    }

    string encrypt(string word1) {
        string s;
        for (auto& ch: word1) {
            if (!this -> map[ch - 'a']) return "";
            s += *(this -> map[ch - 'a']);
        }
        return s;
    }

    int decrypt(string word2) {
        Trie* cur = this -> root;
        for (auto& ch: word2) {
            if (! cur -> child[ch -'a']) return 0;
            cur = cur -> child[ch -'a'];
        }
        return cur -> cnt;
    }
};
