public class Encrypter {

    Dictionary<char, string> keyToValue = new Dictionary<char,string>();
    Dictionary<string,int> fullDict = new Dictionary<string,int>();

    public Encrypter(char[] keys, string[] values, string[] dictionary) {
        for(int i = 0; i < keys.Length; i++) {
            keyToValue[keys[i]] = values[i];
        }

        for (int j = 0; j < dictionary.Length; j++) {
            var word = dictionary[j];
            var encrypted = Encrypt(word);
            var present = fullDict.TryGetValue(encrypted, out var val);
            if (!present) {
                fullDict.Add(encrypted, 1);
            }
            else {
                val++;
                fullDict[encrypted] = val;
            }
        }
    }

    public string Encrypt(string word1) {
        var encStr = string.Empty;
        for (int i = 0; i < word1.Length; i++) {
            var present = keyToValue.TryGetValue(word1[i] , out var val);
            if(!present) {
                encStr += "#";
                // gotta add a random value so that strings with no mapping don't increase count in fullDict by mistake
                // testcase for this is 
                //[[["b"],["ca"],["aaa","cacbc","bbaba","bb"]],["bbb"],["cacaca"]]
            }
            else{
                encStr += val; 
            }          
        }
        return encStr;
    }

    public int Decrypt(string word2) {
        var present = fullDict.TryGetValue(word2, out var count);
        return present ? count : 0;
    }
}

