class Encrypter {
public:
    Encrypter(vector<char>& keys, vector<string>& values, vector<string>& dictionary) {
        for(auto& s:dictionary) add(s);
        vals.resize(128);
        for(int i=0;i<keys.size();i++){
            vals[keys[i]] = values[i];
            decodeMap[values[i]].push_back(keys[i]-'a');
        }
    }

    string encrypt(string word1) {
        string res;
        for(auto c:word1){
            if(vals[c].empty()) return {};
            res += vals[c];
        }
        return res;
    }

    int decrypt(string word2) {
        int res = 0;
        decrypt(word2, 0, root, res);
        return res;
    }

private:
    struct Node{
        bool isWord = false;
        Node* child[26] = {nullptr};
    };

    void add(const string& s){
        auto p = root;
        for(auto c:s){
            if(!p->child[c-'a']) p->child[c-'a'] = new Node();
            p = p->child[c-'a'];
        }
        p->isWord = true;
    }

    void decrypt(const string& s, int i, Node* p, int& res){
        if(!p) return;
        if(i == s.size()){
            if(p->isWord) res++;
            return;
        }
        auto& v = decodeMap[s.substr(i, 2)];
        for(auto c:v){
            decrypt(s, i+2, p->child[c], res);
        }
    }

    Node* root = new Node();
    vector<string> vals;
    unordered_map<string, vector<int>> decodeMap;
};

