class Encrypter {
public:
    vector<string> val;
    unordered_map<string,int> m;
    Encrypter(vector<char>& keys, vector<string>& values, vector<string>& dict) {
        val.resize(26,"");
        for(int i=0;i<keys.size();i++){
            val[keys[i]-'a'] = values[i];
        }
        // we will simply store encrypt string
        for(auto s: dict){
            string tem = "";
            for(auto c: s){
                if(val[c-'a'] == ""){ tem =""; break;}
                tem += val[c-'a'];
            }
            m[tem]++;
        }
    }

    string encrypt(string word1) {
        string ans = "";
        for(auto c: word1){
            if(val[c-'a'] == "") return "";
            ans += val[c-'a'];
        }
        return ans;
    }

    int decrypt(string word2) {
        if(m.find(word2) != m.end()) return m[word2];
        return 0;
    }
};

