    std::map<string, vector<char>> V;
    string M[26];
    int cnt, w2len;

    void insertT(n* T, string& str, char f) {
        for (char c : str) {
            if (T->chi[(c - 'a')] == nullptr) {
                T->chi[(c - 'a')] = new n();
            }
            T = T->chi[(c - 'a')];
        }
        T->flag = f;
    }
    Encrypter(vector<char>& keys, vector<string>& values, vector<string>& dictionary) {
        D = new n();
        V = {};
        for (int i = 0; i < 26; i++) {
            M[i] = "";
        }
        for (int i = 0; i < values.size(); i++) {
            if (V.find(values[i]) == V.end()) {
                V[values[i]] = { keys[i] };
            }
            else {
                V[values[i]].push_back(keys[i]);
            }
            M[(keys[i] - 'a')] = values[i];
        }    
        for (int i = 0; i < dictionary.size(); i++) {
            insertT(D, dictionary[i], '$');
        }
    }

