        else {
            for (char des : V[k]) {
                if (cd->chi[(des - 'a')] != nullptr) {
                    if (idx + 2 == w2len && cd->chi[(des - 'a')]->flag == '$') {
                        cnt++;
                    }
                    else {
                        opt(w2, idx + 2, cd->chi[(des - 'a')]);
                    }
                }
            }
        }
    }

    string encrypt(string word1) {
        string res = word1;
        res = res + word1;
        for (int i = 0; i < word1.length(); i++) {
