                return "";
            }
            res[i * 2] = M[(word1[i] - 'a')][0];
            res[i * 2 + 1] = M[(word1[i] - 'a')][1];
        }
        return res;
    }

    int decrypt(string word2) {
        cnt = 0;
        w2len = word2.length();
        opt(word2, 0, D);
        return cnt;
    }
