var Encrypter = function(keys, values, dictionary) {
    var i;

    this.ks = {};
    this.sk = {};

    let paintTrie = function(word, key) {
        var i;
        let sk = this.sk;

        for( i=0; i<word.length; i++ ) {
            if( !(word[i] in sk) ) sk[word[i]] = {};
            sk = sk[word[i]];
        }
        if( !('key' in sk) ) sk.key = new Set();
        sk.key.add(key);
    }
    var sk;
    for( i=0; i<keys.length; i++ ) {
        this.ks[keys[i]] = values[i];

        paintTrie.bind(this)(values[i], keys[i]);
    }
    this.dict = new Set(dictionary);
    this.dictrie = {};
    var dt, word;
    for( word of this.dict ) {
        dt = this.dictrie;
        for( i=0; i<word.length; i++ ) {
            if( !(word[i] in dt) ) dt[word[i]] = {};
            dt = dt[word[i]];
        }
        dt.valid = true;
    }
};

Encrypter.prototype.encrypt = function(word1) {
    var i, result="";
    for( i=0; i<word1.length; i++ ) {
        result += this.ks[word1[i]];
    }
    return result;
};

Encrypter.prototype.decrypt = function(word2) {
    let ways = function(i=0, ansTrees=null) {
        var j, k, y, z;
        var sk = this.sk;
        var ans = 0;
        var res, subtrees, subkeys;


        if( ansTrees === null ) ansTrees = [this.dictrie];
        if( i >= word2.length ) {
            let count=0;
            for( j=0; j<ansTrees.length; j++ ) {
                if( ansTrees[j].valid === true )
                    count++;
            }
            return count;
        }

        for( j=i; j<word2.length; j++ ) {
            if( !(word2[j] in sk) )
                break;
            sk = sk[word2[j]];

            if( 'key' in sk ) {
                subkeys = [];
                subtrees = [];
                for( res of sk.key ) {
                    for( y=0; y<ansTrees.length; y++ ) {
                        dt = ansTrees[y];
                        for( k=0; k<res.length; k++ ) {
                            if( !(res[k] in dt) ) break;
                            dt = dt[res[k]];
                        }
                        if( k<res.length ) continue;
                        subkeys.push(res);
                        subtrees.push(dt);
                    }
                }
                if( subkeys.length == 0 ) continue;

                let sub = ways.bind(this)( j+1, subtrees );
                ans += sub;
            }
        }

        return ans;
    };

    return ways.bind(this)();
};
