class Encrypter(keys: CharArray, values: Array<String>, dictionary: Array<String>) {

    class TrieNode(val next: Array<TrieNode?> = Array(26) { null }, var isEnd: Boolean = false)

    val forward = mutableMapOf<Char, String>().also {
        keys.forEachIndexed { index, c ->
            it[c] = values[index]
        }
    }
    val backward = mutableMapOf<String, MutableList<Char>>().also {
        values.forEachIndexed { index, str ->
            it[str] = (it[str] ?: mutableListOf()).also {
                it.add(keys[index])
            }
        }
    }

    // for dictionary [a, aba, acba, abaff] trie will be
	//
    //                        *root*
    //                      a (end=true)
    //                b            c
    //        a (end = true)     b
    //          f               a (end = true)
    //            f (end = true)
    //
    val root = TrieNode().also { root ->
        dictionary.forEach {dicStr ->
            var nowItem = root
            dicStr.forEachIndexed { index, ch ->
                if (nowItem.next[ch - 'a'] == null) {
                    TrieNode(isEnd = (index == dicStr.length-1)).also {
                        nowItem.next[ch - 'a'] = it
                        nowItem = it
                    }
                } else {

                    nowItem = nowItem.next[ch - 'a']!!.also {
                        it.isEnd = it.isEnd || (index == dicStr.length-1)
                    }
                }
            }
        }
    }

    fun encrypt(word1: String): String {
        return word1.asSequence().map { forward[it] }.joinToString("")
    }

    fun decrypt(word2: String): Int {
        var cantBeAny = false
        val possChars = word2.chunked(2).asSequence().map {
            backward[it] ?: mutableListOf<Char>().also {
                cantBeAny = true
            }
        }.toList().toTypedArray()

        // odd length or any of pair is not present in 'values' array
        if (cantBeAny) return 0

        var count = 0

        fun tryAdd(nowNode: TrieNode, index: Int) {
            if (index == possChars.size - 1) { // last char checking
                possChars[index].forEach {
                    nowNode.next[it - 'a']?.let { nextNode ->
                        if(nextNode.isEnd) {
                            count++
                        }
                    }
                }
            } else {
                possChars[index].forEach {
                    nowNode.next[it - 'a']?.let { nextNode ->
                        tryAdd(nextNode, index + 1)
                    }
                }
            }
        }

        tryAdd(root, 0)
        return count
    }

}
