class MultiString(val size: Int) {
    private val indexList: Array<MutableSet<Char>> = Array(size) {it -> mutableSetOf<Char>()}

    fun addPossibleCharsAtIndex(chars: Collection<Char>, index: Int) {
        indexList[index].addAll(chars)
    }

    override fun toString(): String {
        return indexList.joinToString(",")
    }
    fun match(other: String): Boolean {
        if(other.length != size) {
            return false
        }
        for(i in 0 until other.length) {
            if(!indexList[i].contains(other[i])) {
                return false
            }
        }
        return true
    }
}

