class Encrypter {

    Map<Character,Integer> map = new HashMap<>();
    Map<String,List<Integer>> valueMap = new HashMap<>();
    char [] keys;
	Trie trie = new Trie();
    String[] values;

    class Trie{
        Map<Character,Trie> children = new HashMap<>();
        boolean leaf = false;

        void insert(String str,int idx){
            if(idx>=str.length()){
                this.leaf = true;
                return;
            }
            Trie child = children.get(str.charAt(idx));
            if(child==null){
                children.put(str.charAt(idx),new Trie());
            }
            children.get(str.charAt(idx)).insert(str,idx+1);
        }

        int search(String str, int idx){
            if(str.length()%2!=0){
                return 0;
            }
            if(idx>=str.length()){
                return this.leaf?1:0;
            }
            int count = 0;
            String next = str.substring(idx,idx+2);
            List<Integer> nextIndicies = valueMap.getOrDefault(next,new ArrayList<>());
            for(Integer nextIndex:nextIndicies){
                if(children.containsKey(keys[nextIndex])){
                    count += children.get(keys[nextIndex]).search(str, idx + 2);
                }
            }
            return count;
        }
    }

    public Encrypter(char[] keys, String[] values, String[] dictionary) {
        this.keys = keys;
        this.values = values;
        for(int i= 0;i<keys.length;i++){
            map.put(keys[i],i);
        }
        for(int i= 0;i<values.length;i++){
            List<Integer> list = valueMap.getOrDefault(values[i],new ArrayList<>());
            list.add(i);
            valueMap.put(values[i],list);
        }

        for(String str : dictionary){
            trie.insert(str,0);
        }
    }

