class Encrypter {
    unordered_map<char, string> map;
    unordered_map<string, vector<char>> reverse_map;
    class trie {
      vector<trie*> next;
      bool end;
    public:
      trie() {
        next.resize(26, nullptr);
        end = false;
      }
      ~trie() {
        for (trie *t : next) delete t;
        next.clear();
      }
      void add(const std::string &s) {
        trie *t = this;
        for (int i = 0; i < s.length(); i++) {
          if (t->next[s[i]-'a'] == nullptr) {
            t->next[s[i]-'a'] = new trie;
          }
          t = t->next[s[i]-'a'];
        }
        t->end = true;
      }
      trie* nextNode(char ch) {
        return next[ch-'a'];
      }
      bool isValid() { return end; }        
    };
    trie t;    
public:
    Encrypter(vector<char>& keys, vector<string>& values, vector<string>& dictionary) {
        for (string &s: dictionary)
          t.add(s);
        for (int i = 0; i < keys.size(); i++) { 
            map[keys[i]] = values[i];
            reverse_map[values[i]].push_back(keys[i]);
        }
    }

    string encrypt(string word1) {
        stringstream ss;
        for (char ch: word1) {
            auto it = map.find(ch);
            if (it != map.end())
                ss << it->second;
            else ss << ch;
        }
        return ss.str();
    }

    void helper(std::string &s, int &cnt, int index, trie* node) {
      if (index >= s.length()) {
        if (node && node->isValid())
            cnt++;
        return;
      }
      string t = s.substr(index, 2);
      for (char ch: reverse_map[t]) {
        trie *t_node = node->nextNode(ch);
        if (t_node != nullptr) {
          helper(s, cnt, index+2, t_node);
        }
      }
    }    
    int decrypt(string word2) {
        int cnt = 0;
        helper(word2, cnt, 0, &t);
        return cnt;
    }
};

