class Encrypter {
    Map<Character, Integer> keysMap;
    Map<String, Set<Integer>> valuesMap;
    char[] keys;
    String[] values;
    Trie trie;
    Map<String, Integer> decMem;
    Map<String, String> encMem;
    public Encrypter(char[] keys, String[] values, String[] dictionary) {
        this.keys = keys;
        this.values = values;
        this.keysMap = new HashMap<>();
        this.valuesMap = new HashMap<>();
        this.trie = new Trie();
        decMem = new HashMap<>();
        encMem = new HashMap<>();
        for(int i=0; i<keys.length; i++){
            keysMap.put(keys[i], i);
        }
        for(int i=0; i< this.values.length; i++){
            Set<Integer> st = valuesMap.getOrDefault(values[i], new HashSet<>());
            st.add(i);
            valuesMap.put(values[i], st);
        }
        for(String s: dictionary){
            trie.add(s);
        }
    }

    public String encrypt(String word1) {
        if(encMem.containsKey(word1)) return encMem.get(word1);
        StringBuilder res = new StringBuilder();
        for(int i=0; i<word1.length(); i++){
            if(!keysMap.containsKey(word1.charAt(i))) return "";
            String repl = this.values[keysMap.get(word1.charAt(i))];
            res.append(repl);
        }
        String ans = res.toString();
        encMem.put(word1, ans);
        return ans;
    } 
     public int decrypt(String word2) {
         if(decMem.containsKey(word2)) return decMem.get(word2);
         int ans = decryptRec(word2, 0, new StringBuilder(), this.trie);
         decMem.put(word2, ans);
         return ans;
     }
    public int decryptRec(String word2, int start, StringBuilder prefix, Trie t) {
        //handle base case
        if(start>=word2.length()) {
            if(this.trie.wordExists(prefix)) return 1;
            return 0;
        }
        String hashed = word2.substring(start, start+2);
        Set<Integer> indcs = valuesMap.getOrDefault(hashed, new HashSet<>());
        int ans = 0;
        for(Integer i: indcs){
            char repl = keys[i];
            prefix.append(repl);
            if(t.links.containsKey(repl)) ans = ans + decryptRec(word2, start+2, prefix, t.links.get(repl));
            prefix.deleteCharAt(prefix.length()-1);
        }
        return ans;
    }
}
class Trie{
    Map<Character, Trie> links = new HashMap<>();
    boolean isTerminal = false;
    String word = null;
