    {
        int ans=0;
        vector<vector<char>> v;        
        for(int i=0;i<word2.length();i+=2)
        {
            string str = word2.substr(i,2);
            vector<char> temp;
            for(char c : ump[str])
            {
                temp.push_back(c);
            }    
            v.push_back(temp);
        }
        int j=0;
        queue<Node*> q;
        q.push(root);
        q.push(nullptr);
        int f = v.size()-1;

        while(j<v.size() && !q.empty())
        {
            Node* temp = q.front();
            if(temp == nullptr)
            {
                j++;
                q.pop();
                if(j!=v.size())
                    q.push(nullptr);
                continue;
            }
            if(j != f)
            {                
                for(char ch : v[j])
                {
                    if(temp->containsKey(ch))
                    {
                        q.push(temp->get(ch));
                    }
                }
            }
            else
            {
                for(char ch : v[j])
                {
                    if(temp->containsKey(ch))
                    {
                        Node* t = temp->get(ch);
                        if(t->flag)
                            ans++;                        
                    }
                }
            }  
            q.pop();
        }

        return ans;
    }
