private static class Node {
	private final String encryptedChar;
	private final Map<String, Node> nexts = new HashMap<>();
	private int wordCount = 0;
	private Node(String encryptedChar) {
		this.encryptedChar = encryptedChar;
	}
}
private final Map<Character, String> forwardMap = new HashMap<>();
private final Node start = new Node(null);
public Encrypter(char[] keys, String[] values, String[] dictionary) {
	for(int i = 0; i < keys.length; i++) {
		forwardMap.put(keys[i], values[i]);
	}
	for(String word : dictionary) {
		Node node = start;
		for(int i = 0; i<word.length(); i++) {
			String encryptedChar = forwardMap.get(word.charAt(i));
			Node nextNode = node.nexts.computeIfAbsent(encryptedChar, key -> new Node(encryptedChar));
			if(i == word.length() - 1) nextNode.wordCount++;
			node = nextNode;
		}
	}
}
public String encrypt(String word1) {
	StringBuffer result = new StringBuffer();
	for(char c : word1.toCharArray()) result.append(forwardMap.get(c));
	return result.toString();
}
public int decrypt(String word2) {
	Node node = start;
	for(int i = 0; i<word2.length() / 2; i++) {
		String encryptedChar = word2.substring(i * 2, i * 2 + 2);
		node = node.nexts.get(encryptedChar);
		if(node == null) return 0;
	}
	return node.wordCount;
}
