type Encrypter struct {
    k2v map[byte]string
    v2k map[string][]byte
    trie *node
}

type node struct {
    next []*node
    end bool
}

func Constructor(keys []byte, values []string, dictionary []string) Encrypter {
    k2v := make(map[byte]string)
    v2k := make(map[string][]byte)

    for i, k := range keys {
        k2v[k] = values[i]
    }

    for i, v := range values {
        v2k[v] = append(v2k[v], keys[i])
    }

    trie := &node{
        next: make([]*node, 26),
        end: false,
    }

    for _, w := range dictionary {
        cur := trie
        for _, r := range w {
            if cur.next[r - 'a'] == nil {
                cur.next[r - 'a'] = &node{
                    next: make([]*node, 26),
                    end: false,
                }
            }
            cur = cur.next[r - 'a']
        }
        cur.end = true
    }

    return Encrypter{
        k2v: k2v,
        v2k: v2k,
        trie: trie,
    }    
}


func (this *Encrypter) Encrypt(word1 string) string {
    var res bytes.Buffer
    for i := 0; i < len(word1); i++ {
        res.WriteString(this.k2v[word1[i]])
    }
    return res.String()
}


func (this *Encrypter) Decrypt(word2 string) int {
    return this.dfs(word2)
}

func (this *Encrypter) dfs(word2 string) int {
    if len(word2) == 0 {
        if this.trie.end {
            return 1
        } else {
            return 0
        }
    }

    res := 0
    for _, b := range this.v2k[word2[:2]] {
        if this.trie.next[b - 'a'] != nil {
            tmp := this.trie
            this.trie = this.trie.next[b - 'a']
            res += this.dfs(word2[2:])
            this.trie = tmp
        }
    }
    return res
}
