  public void insert(String w) { 
    Node n = root;                                       //node for traversal through Trie
    for(int i = 0; i < w.length(); i++){                 
      int id = (int)(w.charAt(i) - 'a');                 //fetch current letter from word and calculate index for it

      if(n.p[id] != null) n = n.p[id];                   //if this letter is presented in table move to next
      else                                               //if not 
        for(; i != w.length(); i++){                     //create new nodes
          id = (int)(w.charAt(i) - 'a');
          n.p[id] = new Node();
          n = n.p[id];
        }
    }
    n.eow = true;                                        //check last node as end of word
  }

  Map<Character, String> en;                             //for encoding strings
  Map<String, List<Integer>> de;                         //{string, list of indexes of letters with this string} 
  Map<String, Integer> answeres;                         //for coinciding enquiries
  int ans;                                               //for counting all possibilities for each enquiry 

  public Encrypter(char[] keys, String[] values, String[] dictionary) {
    en = new HashMap<>();
    de = new HashMap<>();
    answeres = new HashMap<>();
    root = new Node();                                   //construct new Trie
    root.eow = true;                                     //and put string ""


    for(int i = 0; i != keys.length; i++){               //fill our hashmaps
      en.put(keys[i], values[i]);
      if(!de.containsKey(values[i])){
        List<Integer> list = new ArrayList<>();
        de.put(values[i], list);
      }
      de.get(values[i]).add(keys[i] - 'a');
    }

    for(int i = 0; i != dictionary.length; i++)          //and construct Trie
      insert(dictionary[i]);
  }

  public String encrypt(String word1) {                  //construct answer
    String ans = "";
    for(int i = 0; i != word1.length(); i++)
      ans += en.get(word1.charAt(i));

    return ans;
  }

  public int decrypt(String word2) {
    if(answeres.containsKey(word2)) return answeres.get(word2);   //if we already have this inquiry

    ans = 0;                                                      //refrash counter of all possibilities
    dfs(0, root, word2);                                          //dfs
    answeres.put(word2, ans);                                     //put this answer for next inquiries

    return ans;   
  }

  public void dfs(int i, Node node, String w){                    //dfs
    String key = w.substring(i, i + 2);                           //fetch current substring
    if(de.containsKey(key))                                       //if we have keys for this substring
      for(int j = 0; j != de.get(key).size(); j++)                //try to move through Trie
        if( node.p[de.get(key).get(j)] != null) {                 //if we dont have node in Trie
          if(i + 2 == w.length()){                             //if we reach end of current word
            if(node.p[de.get(key).get(j)].eow == true) ans++;     //check : have we this word in Trie?
          }
          else dfs(i+2, node.p[de.get(key).get(j)], w);        //if we dont reach
    }
  }

}
