func Constructor(keys []byte, values []string, dictionary []string) Encrypter {
	dictionaryHelper := make(map[int]map[string]bool)
	for _, v := range dictionary {
		if dictionaryHelper[len(v)] == nil {
			dictionaryHelper[len(v)] = make(map[string]bool)
		}
		for i := 1; i <= len(v); i++ {
			dictionaryHelper[len(v)][v[:i]] = true
		}
	}
	encryptMap := make(map[string]string)
	decryptMap := make(map[string][]string)
	for i, v := range keys {
		encryptMap[string(v)] = values[i]
		decryptMap[values[i]] = append(decryptMap[values[i]], string(v))
	}
	return Encrypter{
		dictionary: dictionaryHelper,
		encryptMap: encryptMap,
		decryptMap: decryptMap,
	}
}

func (e *Encrypter) Encrypt(word1 string) string {
	sb := strings.Builder{}
	for _, v := range word1 {
		sb.WriteString(e.encryptMap[string(v)])
	}
	return sb.String()
}

func (e *Encrypter) Decrypt(word2 string) int {
	dictionary := e.dictionary[len(word2)/2]
	current := make([]string, 0, len(e.decryptMap[word2[:2]]))
	for _, v := range e.decryptMap[word2[:2]] {
		if dictionary[v] == true {
			current = append(current, v)
		}
	}
	for i := 2; i < len(word2) && len(current) > 0; i += 2 {
		alphas := e.decryptMap[word2[i:i+2]]
		next := make([]string, 0, len(current))
		for _, alpha := range alphas {
			for _, pre := range current {
				newWord := pre + alpha
				if dictionary[newWord] == true {
					next = append(next, newWord)
				}
			}
		}
		current = next
	}
	return len(current)
}
