    def __init__(self, keys: List[str], values: List[str], dictionary: List[str]):
        self.pair,self.de = defaultdict(str),defaultdict(int)
        for i,j in enumerate(keys):
            self.pair[j]=values[i]
        for i in dictionary:
            key = "".join(self.pair[j] for j in i)
            self.de[key]+=1

    def encrypt(self, word1: str) -> str:
        return "".join(self.pair[i] for i in word1)

    def decrypt(self, word2: str) -> int:
        return self.de[word2]
