class Encrypter {
    Map<Character, String> keysToValues  = new HashMap();
    Map<String, List<Character>> valuesToKeys = new HashMap();
    TrieNode root = new TrieNode();
    public Encrypter(char[] keys, String[] values, String[] dictionary) {
        int i  = 0;
        for(char key : keys){
            keysToValues.put(key, values[i++]);
        }
        for(i = 0; i < values.length; i++){
            valuesToKeys.putIfAbsent(values[i], new ArrayList());
            valuesToKeys.get(values[i]).add(keys[i]);
        }

        for(String word : dictionary){
            construct(root, word);
        }
    }

    private void construct(TrieNode root, String word){
        for(int i = 0; i< word.length(); i++){
            char c = word.charAt(i);
            if(root.children.containsKey(c)){
                root = root.children.get(c);
            }else{
                TrieNode node = new TrieNode();
                root.children.put(c, node);
                root = node;
            }
        }

        root.isWord = true;
    }
    public String encrypt(String word1) {
        StringBuilder sb = new StringBuilder();

        for(int i = 0; i< word1.length(); i++){
            char c = word1.charAt(i);
            if(keysToValues.get(c) != null){
                sb.append(keysToValues.get(c));
            }else{
                sb.append(c);
            }
        }
        return sb.toString();
    }

    public int decrypt(String word2) {
       return count(root, word2, 0);
