class Encrypter {
    Map<Character,String> enc;
    Map<String,Integer> encDict;

    public Encrypter(char[] keys, String[] values, String[] dictionary) {
        int n= keys.length;
        this.enc= new HashMap<>();
        for(int i=0; i<n; i++) enc.put(keys[i], values[i]);

        this.encDict= new HashMap<>();
        for(String word:dictionary){
            String encWord= encrypt(word);
            encDict.put(encWord, 1+encDict.getOrDefault(encWord, 0));
        }
    }

    public String encrypt(String word1) {
        char[] ca= word1.toCharArray();
        StringBuilder sb= new StringBuilder();
        for(char c:ca) sb.append(enc.get(c));
        return sb.toString();
    }

    public int decrypt(String word2) {
        return encDict.getOrDefault(word2, 0);
    }
}

