class Trie{
private:
    unordered_map<string, Trie*> next; 
    bool isWord = false;
    int match = 0;
public:
    Trie(){}
    void insert(string word,vector<string>& ktov)
    {
        Trie* node = this;
        for(char& c:word)
        {
            if(node->next[ktov[c-'a']]==nullptr)
                node->next[ktov[c-'a']] = new Trie();
            node = node->next[ktov[c-'a']];
        }
        node->isWord = true;
        node->match++;
    }
    int search(string word)
    {
        Trie* node = this;
        for(int i=0;i<word.size();i+=2)
        {
            string now = word.substr(i,2);
            if(node->next[now]==nullptr)
                return 0;
            node = node->next[now];
        }
        return node->match;
    }
};
