public:
	bool isEnd;
	Node* arr[26];

	Node(){
		isEnd = false;
		for(int i=0;i<26;i++) {
			arr[i] = NULL;
		}
	}
};

class Encrypter {
	Node* root;
	unordered_map<char,string> keys;
	unordered_map<string,vector<char> > values;

	void insert(string& s){
		Node* t = root;
		for(int i=0;i<s.size();i++) {
			if(t->arr[s[i] - 'a'] == NULL) {
				t->arr[s[i] - 'a'] = new Node();
			}            
			t = t->arr[s[i]-'a'];
		}
		t->isEnd = true;
	}

	int decrypt(string& word,int i,Node* t){
		if(i >= word.size()) {
			return t->isEnd;
		}
		string s = word.substr(i,2);
		int ans = 0;        
		for(char& ch:values[s]) {
			if(t->arr[ch - 'a'] == NULL) continue;
			ans += decrypt(word, i+2, t->arr[ch - 'a']);
		}
		return ans;
	}
public:
