        map2 = new HashMap<>();
        map3 = new HashMap<>();
        for(int i=0;i<keys.length;i++) {
            map1.put(keys[i],values[i]);
            if(map2.containsKey(values[i])) {
                ArrayList<Character> list = map2.get(values[i]);
                list.add(keys[i]);
                map2.put(values[i],list);
            } else {
                ArrayList<Character> list = new ArrayList<>();
                list.add(keys[i]);
                map2.put(values[i],list);
            }
