**if at any point open < 0 then the path is not balanced.**

```
class Solution {
    int m, n;
    int memo[101][101][201];
public:
    bool doit(vector<vector<char>>& grid, int i, int j, int open) {

        open += grid[i][j] == '(' ? 1 : -1;
        if (open < 0)   return false;
        if (memo[i][j][open] != -1) return memo[i][j][open];
        if (i == m-1 and j == n-1 and open == 0)    return true;
        if (i < m-1 and doit(grid, i+1, j, open))   return memo[i][j][open] = true;
        if (j < n-1 and doit(grid, i, j+1, open))   return memo[i][j][open] = true;
        return memo[i][j][open] = false;
    }
    bool hasValidPath(vector<vector<char>>& grid) {

        memset(memo, -1, sizeof memo);
        m = size(grid), n = size(grid[0]);
        if((m+n-1)&1) return false;
        return doit(grid, 0, 0, 0);
    }
};
```

