class Solution {
public:
    bool hasValidPath(vector<vector<char>>& grid) {

        queue<tuple<int,int,int>> q;
        int visited[100][100][101] = {};

        int row=grid.size();
        int col=grid[0].size();

        if(grid[0][0]=='(') q.push({0,0,1});
        else return false;

        while(!q.empty()){

            auto [Nx,Ny,b]=q.front();
            q.pop();

            if(b<0 or b>(row+col)/2 or visited[Nx][Ny][b]) continue;
            visited[Nx][Ny][b] = true;

            if(Nx==row-1 and Ny==col-1 and b==0) return true;

            if(Nx+1<row){
                if(grid[Nx+1][Ny]=='(') q.push({Nx+1,Ny,b+1});
                else q.push({Nx+1,Ny,b-1});
            }
            if(Ny+1<col){
                if(grid[Nx][Ny+1]=='(') q.push({Nx,Ny+1,b+1});
                else q.push({Nx,Ny+1,b-1});
            }

        }
        return false;

    }
};
