class Solution:
    def hasValidPath(self, grid: List[List[str]]) -> bool:
        if grid[0][0] == ')':
            return False

        m, n = len(grid), len(grid[0])
        DIRS = [[0,1],[1,0]]

        stack = []

        # bal for tracking parenthesis balance
        bal = 1
        stack.append([0, 0, bal])
        seen = set()
        seen.add((0, 0, bal))
        while stack:
            x, y, curr_bal = stack.pop()
            if x == m - 1 and y == n - 1 and curr_bal == 0:
                return True
            curr = grid[x][y]
            for d in DIRS:
                r = x + d[0]
                c = y + d[1]
                if 0<=r<m and 0<=c<n:
                    if (r,c,curr_bal) not in seen:
                        seen.add((r,c,curr_bal))
                        next_p = grid[r][c]
                        if next_p == "(":
                            curr_bal += 1
                            stack.append([r, c, curr_bal])
                            curr_bal -= 1
                        else:
                            if curr_bal > 0:
                                curr_bal -= 1
                                stack.append([r, c, curr_bal])
                                curr_bal += 1
        return False
