class Solution {
public:
    bool dfs(int i,int j,int n,int m,int score,vector<vector<int>>& g,vector<vector<vector<int>>>& dp){
        if(i>=n || j>=m) return false;
        score+=g[i][j];
        if(i==n-1 && j==m-1) return score==0;
        if(score<0) return false;
        if(dp[i][j][score] != -1) return dp[i][j][score];
        return dp[i][j][score]=dfs(i+1,j,n,m,score,g,dp) || dfs(i,j+1,n,m,score,g,dp);
    }
    bool hasValidPath(vector<vector<char>>& grid) {
        int n=grid.size(),m=grid[0].size();
        if((n+m-1)&1) return false;
        vector<vector<int>> g(n,vector<int>(m,-1));
        vector<vector<vector<int>>> dp(n,vector<vector<int>>(m,vector<int>(n+m+5,-1)));
        for(int i=0;i<n;i++)
            for(int j=0;j<m;j++)
                if(grid[i][j]=='(') g[i][j]=1;
        return dfs(0,0,n,m,0,g,dp);
    }
};
