public class Solution {

	private bool[,,] _visited;
	private int _rowLength;
	private int _colLength;
	private int _midLength;
	private char[][] _grid;

	public bool ValidPath(int row, int col,int bal){
		bal += _grid[row][col]=='('?1:-1;
		if(bal < 0 || bal > _midLength || _visited[row,col,bal])
			return false;

		_visited[row,col,bal]=true;
		if(row == _rowLength-1 && col == _colLength-1){
			if(bal == 0)
				return true;
			else
				return false;
		}

		if(row<_rowLength-1 && ValidPath(row+1,col,bal))
			return true;
		if(col<_colLength-1 && ValidPath(row,col+1,bal))
			return true;

		return false;
	}
	public bool HasValidPath(char[][] grid) {
		_rowLength = grid.Length;
		_colLength = grid[0].Length;
		_midLength = (_rowLength+_colLength)/2;
		_visited = new bool[_rowLength,_colLength,_midLength+1];
		_grid = grid;

		return ValidPath(0,0,0);
	}
}

