class Solution {
public:
    bool hasValidPath(vector<vector<char>>& grid) {
        int m = grid.size();
        int n = grid[0].size();
        if (grid[m-1][n-1] == '(' || (m+n)%2 == 0)
            return false;
        vector<vector<unordered_set<int>>> dp(m+1, vector<unordered_set<int>>(n+1));
        dp[m][n-1].insert(0);
        dp[m-1][n].insert(0);
        for (int i = m-1; i >= 0; --i) {
            for (int j = n-1; j >= 0; --j) {
                int p = (grid[i][j] == ')') ? 1 : -1;
                for (int v: dp[i+1][j]) {
                    v += p;
                    if (v >= 0)
                        dp[i][j].insert(v);
                }
                for (int v: dp[i][j+1]) {
                    v += p;
                    if (v >= 0)
                        dp[i][j].insert(v);
                }
            }
        }
        return dp[0][0].find(0) != dp[0][0].end();
    }
};
