class Solution {
public:
    bool solve(int r, int c, int count, vector<vector<char>> &grid, vector<vector<vector<int>>> &dp)
{
    int n = grid.size();
    int m = grid[0].size();
    if (r == n - 1 and c == m - 1)
    {
        return count == 1;
    }

    if (r < 0 or r >= n or c < 0 or c >= m or count < 0 or count > ((m + n) / 2))
    {
        return false;
    }

    if (dp[r][c][count] != -1)
    {
        return dp[r][c][count];
    }

    int tot = count;
    if (grid[r][c] == '(')
    {
        tot++;
    }
    else
    {
        tot--;
    }

    bool right = solve(r, c + 1, tot, grid, dp);
    bool down = solve(r + 1, c, tot, grid, dp);

    return dp[r][c][count] = right or down;
}

bool hasValidPath(vector<vector<char>> &grid)
{
    int n = grid.size();
    int m = grid[0].size();
    if (n % 2 == m % 2)
    {
        return false;
    }
    if (grid[0][0] == ')' or grid[n - 1][m - 1] == '(')
    {
        return false;
    }
    vector<vector<vector<int>>> dp(n, vector<vector<int>>(m, vector<int>(((m + n) / 2) + 1, -1)));

    return solve(0, 0, 0, grid, dp);
}
};
