class Solution {
public:
    bool helper(int i, int j, int count, vector<vector<char>>& grid,  vector< vector< vector<int> > > &dp) {
        if(i == grid.size() || j == grid[0].size()) return false;
        if(i == grid.size() -1 && j == grid[0].size() - 1) {
            if(grid[i][j] == '(') return false;
            return count - 1 == 0;
        }
        if(dp[i][j][count] != -1) return dp[i][j][count];

        if(grid[i][j] == '(') {
            return dp[i][j][count] = helper(i+1, j, count+1, grid, dp) || helper(i, j+1, count+1, grid, dp);
        }
        if(count > 0) return dp[i][j][count] = helper(i+1, j, count-1, grid, dp) || helper(i, j+1, count-1, grid, dp);

        return dp[i][j][count] =false;

    }
    bool hasValidPath(vector<vector<char>>& grid) {
        vector< vector< vector<int> > > dp(grid.size(), vector< vector< int > > (grid[0].size(), vector< int > (grid.size()+grid[0].size(), -1)));
        return helper(0, 0, 0, grid, dp);
    }
};
