    bool hasValidPath(vector<vector<char>>& grid) {
        int n=grid.size();
        int m=grid[0].size();
        vector<vector<vector<int>>>memo(n,vector<vector<int>>(m,vector<int>((n+m)/2+1,-1)));
        if(grid[n-1][m-1]=='(')return false;

        return helper(0,0,0,grid,memo);

    }
};
