class Solution:
    def hasValidPath(self, grid: List[List[str]]) -> bool:
        m,n=len(grid), len(grid[0])

        # first value in the grid should be '(' and last value be ')'
        if grid[0][0]!='(' or grid[-1][-1]!=')':
            return False

        @lru_cache(None)
        def dp(i,j,c):
            # When we reach the last cell, the number of open brackets should be 1.
            # ')' from last cell balances it
            if i==m-1 and j==n-1:
                if c==1:
                    return True
                else:
                    return False
            # i,j should be within the range of m,n
            if i>=m or j>=n:
                return False

            res=False
            # From every cell, we can move right(j+1) or down(i+1)
            if grid[i][j]=='(':
                # When we encounter '(' we increment count by 1
                res=res or dp(i+1, j, c+1) or dp(i, j+1, c+1)
            else:
                # When we encounter ')' number of of open brackets must be greater than 0
                if c>0:
                    # Decrement the count by 1
                    res=res or dp(i+1, j, c-1) or dp(i, j+1, c-1)
            return res
        return dp(0,0,0)

